import React, { useState,useMemo } from 'react'

const FilterList = () => {
    const [search, setSearch] = useState("");

    // Sample list of numbers
    const numbers = [12, 34, 56, 78, 91, 23, 45, 67, 89, 10, 30, 50];

    // useMemo ensures filtering is re-calculated only when 'search' changes
    const filteredNumbers = useMemo(() => {
        console.log("Filtering numbers..."); // Logs only when filtering runs
        return numbers.filter(num => 
            num.toString().includes(search)
        );
    }, [search, numbers]); 

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>useMemo Example: Optimized Filtering</h2>
            <input 
                type="text" 
                placeholder="Search numbers..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                style={{
                    padding: "10px",
                    fontSize: "16px",
                    marginBottom: "10px",
                    borderRadius: "5px",
                    border: "1px solid #ccc"
                }}
            />
            <ul style={{ listStyle: "none", padding: 0 }}>
                {filteredNumbers.map((num, index) => (
                    <li key={index} style={{ fontSize: "18px", margin: "5px 0" }}>
                        {num}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default FilterList
